import socket
import time


def test():
  sckt = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
  sckt.setsockopt(socket.SOL_SOCKET, socket.SO_BROADCAST, True)
  sckt.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, True)
  sckt.bind(('172.28.72.64', 60013))
  addr = ('255.255.255.255', 60013)
  senddata = ''''''
  while (1):
    sckt.sendto(senddata.encode(), addr)
    time.sleep(0.001)


if (__name__ == '__main__'):
  test()
